/* Start */

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box
}

html {
    font-size: 10px;
    font-family: 'Montserrat', sans-serif;
    color: #eee;
}

body {
    font-family: 'Montserrat', sans-serif;
    overflow-x: hidden;
    background-image: url("images/globe1.jpg");
    background-repeat: no-repeat;
    background-position: center;
    background-size: cover;
    background-attachment: fixed;
    height: 100%;
    width: 100%;
}

a {
    text-decoration: solid;
    color: whitesmoke;
}

p {
    font-family: 'Roboto', sans-serif;
    font-size: 1.5rem;
    font-weight: 300;
    color: white;
}

/* Header */

header {
    height: 100vh;
    width: 100%;
}

/* Hamburger menu */

.top-nav {
    background-color: rgba(0, 0, 0, 0.9);
    border-bottom-right-radius: 90%;
    border-bottom-left-radius: 90%;
    height: 100vh;
    width:100%;
    position: fixed;
    top: -100vh;
    transition: all .5s ease-in-out;
    z-index: 50;
}

.change {
    top: 0;
}

.menu {
    position: fixed;
    top: 4rem;
    right: 4rem;
    height: 3rem;
    width: 4rem;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    cursor: pointer;
}

.bar {
    background-color: rgb(233, 234, 236);
    height: 2px;
    width: 100%;
    border-radius: .8rem;
    transition: all 0.5s;
}

.change .bar.start {
    transform: rotate(65deg) translate(25px, -21px);
}

.change .bar.middle {
    transform: translateX(-50px);
    background: transparent;
    box-shadow: none;
}

.change .bar.end {
    transform: rotate(-65deg) translate(25px, 21px);
}

.menu-list {
    list-style: none;
    height: 100%;
    width: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
}

li {
    margin: 0 2rem;
}

.nav-link {
    font-family: 'Montserrat', sans-serif;
    font-size: 5rem;
    padding: 1rem;
}

.nav-link:hover,
.nav-link:focus {
    background: linear-gradient(to top, rgb(189, 198, 247), rgb(6, 1, 65));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}

.news-ticker {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 100%;
    text-align: center;
    z-index: 1;
}

/* Main title */

.title-1 {
    background-color: rgba(0, 0, 0, 0.07);
}

.title-1 h4 {
    font-size: 10rem;
    font-family: 'Montserrat', sans-serif;
    font-weight: bolder;
    background-color: rgba(230, 225, 225, 0.9);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    padding: 1rem;
}

.btn-sm {
    border-width: thin;
    border-radius: 0;
}

.out {
    font-size: 0.9rem;
    border: 1px solid white;
    color: white;
    font-weight: bolder;
    text-shadow: .1rem .1rem .5rem black;
}

/* Image Slider (Carousel) */

.carousel-item {
    height: 100vh;
    position: center;
    background-size: 110%;
}

.carousel-caption {
    position: absolute;
    top: 38%;
    text-transform: uppercase;
    display: table;
    text-align: center;
    margin: auto;
}

.carousel-caption h1 {
    font-size: 6rem;
    font-weight: 700;
    letter-spacing: .3rem;
    text-shadow: .1rem .1rem .8rem black;
    padding-bottom: 1rem;
    vertical-align: middle;
    height: 100%;
}

.carousel-caption h3{
    font-size: 3rem;
    font-weight: bold;
    text-shadow: .1rem .1rem .5rem black;
    padding-bottom: 1.6rem;
    color: white;
}

.overlay {
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0;
    left: 0;
    background-color: rgba(0,0,0,0.4);
}

.btn-lg {
    border-width: medium;
    border-radius: 0;
    font-size: 1.1rem;
}

.outline {
    border: 2px solid white;
    color: white;
    font-size: 2rem;
    font-weight: bolder;
    text-shadow: .1rem .1rem .5rem black;
}

.outline:hover {
    background-color: white;
    transition: 0.3s ease background-color;
    text-shadow: none;
}

/* Bootstrap Grid */

.my-container {
    background-color: rgba(0, 0, 0, 0.9);
}

.my-row {
    padding: 70px 0px;
}

/* Hover effect for images in grid */

.thumbnail {
    height: 100%;
    width: 100%;
    overflow: hidden;
}

img {
    -webkit-transition: all 0.5s ease;
}

img:hover {
    transform: scale(1.4);
}

/* Contact frame */


.contact-details {
    text-align: center;
    margin-bottom: 1rem;
    background-color: rgba(0, 0, 0, 0.9);
}

.contact-details h1 {
    font-size: 3.5rem;
    text-transform: uppercase;
    font-weight: bolder;
    position: relative;
    margin-bottom: 1rem;
    color: whitesmoke;
    opacity: .9;
}

form {
    width: 70%;
    margin: 0 auto;
}

form label {
    font-size: 1.5rem;
    font-weight: bold;
    text-transform: uppercase;
}

input,
select,
textarea {
    width: 100%;
    padding: 1rem;
    margin-bottom: 5px;
    border: none;
    outline-color: rgba(0, 0, 0, 0.9);
}

input[type="submit"] {
    width: initial;
    background-color: rgba(0, 0, 0, 0.9);
    color: white;
    font-size: 1.8rem;
    font-weight: bolder;
    text-transform: uppercase;
    padding: 1rem 2rem;
    border-radius: .5rem;
    cursor: pointer;
    transition: background-color 650ms;
}

input[type="submit"]:hover {
    color: black;
    background-color: rgba(230, 225, 225, 0.9);
}

.contact-frame {
    padding: 10px;
    background-color: rgba(0, 0, 0, 0.5);
    width: 100%;
    color: white;
}

/* Footer */

.copyright {
    background-color: rgba(0, 0, 0, 0.05);
    position: relative;
    width: 100%;
    text-align: center;
    padding: 2rem 0;
}

.copyright p {
    font-family: 'Roboto', sans-serif;
    font-size: 1.4rem;
    color: white;
}

#up {
    position: fixed;
    bottom: 40px;
    right: 40px;
    font-size: 35px;
    width: 50px;
    height: 50px;
    background: rgba(0, 0, 0, 0.9);
    color: white;
    border: none;
    cursor: pointer;
}


/* Media quaries */
